const menuLinks=document.querySelectorAll(".menu_link");menuLinks.forEach((e=>{e.addEventListener("click",(function(){menuLinks.forEach((e=>e.classList.remove("active"))),this.classList.add("active")}))}));const images=document.querySelectorAll(".main_images img"),prevButton=document.querySelector(".main_arrows .prev"),nextButton=document.querySelector(".main_arrows .next");let currentIndex=0;function showCurrentImage(){images.forEach(((e,t)=>{t===currentIndex?e.classList.remove("hidden"):e.classList.add("hidden")}))}prevButton.addEventListener("click",(()=>{currentIndex=0===currentIndex?images.length-1:currentIndex-1,showCurrentImage()})),nextButton.addEventListener("click",(()=>{currentIndex=currentIndex===images.length-1?0:currentIndex+1,showCurrentImage()})),showCurrentImage();const scrollBtn=document.querySelector(".main_btn"),targetBlock=document.querySelector("#products");function smoothScroll(e){const t=e.getBoundingClientRect().top+window.scrollY;window.scrollTo({top:t,behavior:"smooth"})}scrollBtn.addEventListener("click",(e=>{e.preventDefault(),smoothScroll(targetBlock)}));const hamburger=document.querySelector(".hamburger"),menu=document.querySelector(".menu");hamburger.addEventListener("click",(()=>{hamburger.classList.toggle("active"),menu.classList.toggle("active")}));